        websocket_url = self.base_url.replace("https://", "wss://").replace("http://", "ws://") + "/api/ai-room/ws"
        
        try:
            # Simple connectivity test - we expect this to fail with a specific WebSocket error
            # rather than a 404 or connection refused
            import socket
            import ssl
            from urllib.parse import urlparse
            
            parsed_url = urlparse(websocket_url)
            host = parsed_url.hostname
            port = parsed_url.port or (443 if parsed_url.scheme == "wss" else 80)
            
            # Test basic connectivity to the host/port
            sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            sock.settimeout(5)
            
            if parsed_url.scheme == "wss":
                context = ssl.create_default_context()
                sock = context.wrap_socket(sock, server_hostname=host)
            
            result = sock.connect_ex((host, port))
            sock.close()
            
            if result == 0:
                print(f"‚úÖ WebSocket endpoint is accessible at {websocket_url}")
                print(f"   - Host connectivity: ‚úÖ")
                print(f"   - Port accessibility: ‚úÖ")
            else:
                print(f"‚ö†Ô∏è WebSocket endpoint connectivity issue (code: {result})")
                
        except Exception as e:
            print(f"‚ö†Ô∏è WebSocket connectivity test failed: {str(e)}")
