        self.assertEqual(visite_total, expected_visite_total, f"Visite total should be {visite_count} √ó 65 = {expected_visite_total} TND")
        
        # Verify controle calculations (should be 0 TND)
        self.assertEqual(controle_total, 0.0, "Controle total should be 0 TND (free)")
        
        # Verify overall total
        total_encaisse = visite_total + controle_total
        self.assertEqual(total_encaisse, expected_visite_total, f"Total encaiss√© should be {expected_visite_total} TND")
        
        print(f"‚úÖ Payment calculations verified: {visite_count} visites √ó 65 TND = {visite_total} TND, {controle_count} contr√¥les √ó 0 TND = {controle_total} TND")
    
